// ===========================================================================//	CommonCarbonPrefix.h		©1999-2000 Metrowerks Inc. All rights reserved.// ===========================================================================//	Common options for a Carbon Target// ---------------------------------------------------------------------------//	Carbon Target#define PP_Target_Carbon		1#define PP_Target_Classic		(!PP_Target_Carbon)#define TARGET_API_MAC_CARBON	PP_Target_Carbon#define TARGET_API_MAC_OS8		PP_Target_Classic// ---------------------------------------------------------------------------//	Options	#define PP_Uses_PowerPlant_Namespace	0#define PP_StdDialogs_Option			PP_StdDialogs_NavServicesOnly#define PP_Uses_Aqua_MenuBar			1#define PP_Warn_Obsolete_Classes		1#define PP_Suppress_Notes_22			1	// Make sure the key debug macro is defined (this is originally #defined	// in PP_Debug.h, but we #define it ourselves in the enclosing prefix	// file to use it as our "switch" macro for the debug and release builds).	#ifndef PP_Debug	#error "PP_Debug is not yet #defined"#endif#define	PP_MenuUtils_Option					1	// classic	// For the MSL#define	__dest_os							__mac_os	// Set some PP Debugging macros#define	PP_Debug_Obsolete_Support			0		// No support for obsolete stuff#if PP_Debug		// Of course these PowerPlant macros need to be turned on	#define Debug_Throw	#define Debug_Signal			// Ensure all the PP debugging macros are set as we want		// them to be. Non-Metrowerks supports are turned off by		// default in the project stationery.	#define	PP_MoreFiles_Support			0	#define	PP_Spotlight_Support			0	#define	PP_QC_Support					0	#define PP_DebugNew_Support				1		// Set DebugNew to full strength	#define DEBUG_NEW						DEBUG_NEW_LEAKS#else		// Not debugging, so make sure everything is off		// (only used for final builds)	#define	PP_MoreFiles_Support			0	#define	PP_Spotlight_Support			0	#define	PP_QC_Support					0	#define PP_DebugNew_Support				0			// DebugNew should be off.	#define DEBUG_NEW						DEBUG_NEW_OFF#endif	// Tho one would normally conditionalize this #include with	// a check for PP_DebugNew_Support, DebugNew.h must always be	// brought in else use of the NEW macro will generate compile	// errors in final targets. However, it must be conditionalized	// by the C++ check to avoid being pulled into C sources and	// confusing the compiler.	#ifdef __cplusplus	#include "DebugNew.h"#endif